#!/usr/bin/env bash
set -e

echo "==> Installing packages..."
sudo apt-get -qq update
sudo apt-get -yqq install nginx

uuid=$(cat /proc/sys/kernel/random/uuid | cut -d'-' -f1)
cat > /usr/share/nginx/html/index.html <<EOF
<html>
<head>
  <title>Terraform + Azure ($uuid)</title>
  <link href="https://fonts.googleapis.com/css?family=Open+Sans" rel="stylesheet">
  <link href="https://fonts.googleapis.com/css?family=Droid+Sans+Mono" rel="stylesheet">
  <style type="text/css">
    html,body{
      height: 100%;
      width: 100%;
    }
    body {
      background-color: #000;
      background-repeat: repeat;
      background-size: 150px auto;
      font-family: 'Open Sans', sans-serif;
    }
  </style>
</head>
<body>
  <div id="parent">
    <div id="child">
      <span id="purple-header"></span>
      <div id="top-text">
        Terraform <span>Webinar</span>
      </div>
      <div id="content">
        <span id="under">I am node</span> <tt>$uuid</tt>
        <p>
          Refresh the page to see traffic routed to other nodes behind the
          load balancer.
        </p>
    </div>
        
    </div>
  </div>
</body>
EOF

cat > /tmp/favicon.ico.b64 <<EOF

EOF
base64 -d /tmp/favicon.ico.b64 > /usr/share/nginx/html/favicon.ico
